import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import makeAnimated from "react-select/animated";
import { default as ReactSelect } from "react-select";
import { DateRangePicker } from 'react-date-range';
import { components } from "react-select";
import {Button} from 'react-bootstrap';
import {
    BrowserRouter as Router ,
    Routes,
    Route,
    Link,
    useParams

  } from 'react-router-dom' ;


  const colourOptions = [
    { value: "India", label: "India" },
    { value: "China", label: "China" },
    { value: "Russia", label: "Russia" },
    { value: "Australia", label: "Australia"},
    { value: "Canada", label: "Canada"}
  ];

  const statOptions = [
    { value : 1 , label : "Deaths" },
    { value : 2, label : "Active cases"}


  ]

  const Option = (props) => {
    return (
      <div>
        <components.Option {...props}>
          <input
            type="checkbox"
            checked={props.isSelected}
            onChange={() => null}
          />{" "}
          <label>{props.label}</label>
        </components.Option>
      </div>
    );
  };


class Stats_info extends React.Component{

    constructor(props){

        super(props); 

        this.state = {
            name : "India",
            Country_Selected: [],
            Stat_options : [],
            all_req : false,
            date_range : []

        }




        console.log("This is stats constructor") ;

        this.get_data = this.get_data.bind(this) ;

    }

    handleChange = selected => {
        this.setState({
          Country_Selected: selected
        });

        console.log(this.state.optionSelected) ;
        console.log(selected) ;



      };

    handleChange2 = selected => {
      this.setState({

        Stat_options : selected


      })


    }

    handleSelect(date){
      console.log(date); // native Date object

      this.setState({
        date_range: date
      })


  }


    get_data(){


      console.log("called get data") ;

      console.log(this.state.Country_Selected) ;

      console.log(this.date_range) ;




    }

    componentDidMount(){

        // do nothing as of now 

    }

    render(){
      const selectionRange = {
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
      }

        return(

            <>



                <h1> Stats page </h1>


                <Button onClick={this.get_data} > Get the data</Button>
                <p>Select Countries</p>
                <span
        class="d-inline-block"
        data-toggle="popover"
        data-trigger="focus"
        data-content="Please selecet account(s)"
      >
        <ReactSelect
          options={colourOptions}
          isMulti
          closeMenuOnSelect={false}
          hideSelectedOptions={false}
          components={{
            Option
          }}
          onChange={this.handleChange}
          allowSelectAll={true}
          value={this.state.optionSelected}
        />
      </span>

          <br></br>
          <p>Select Statistic</p>
      <span
        class="d-inline-block"
        data-toggle="popover"
        data-trigger="focus"
        data-content="Please selecet account(s)"
      >
        <ReactSelect
          options={statOptions}

          closeMenuOnSelect={false}
          hideSelectedOptions={false}
          components={{
            Option
          }}
          onChange={this.handleChange2}

          value={this.state.Stat_options}
        />
      </span>

      <p> Select the date range </p>
      <DateRangePicker
        ranges={[selectionRange]}
        onChange={this.handleSelect}
      />



            </>





        )
    }

}



export default (props) => (
    <Stats_info
        {...props}
        params={useParams()}
    />
);
