import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import makeAnimated from "react-select/animated";
import { default as ReactSelect } from "react-select";
import { DateRangePicker } from 'react-date-range';
import { components } from "react-select";
import {Button} from 'react-bootstrap';
import {
    BrowserRouter as Router ,
    Routes,
    Route,
    Link,
    useParams

  } from 'react-router-dom' ;

  import 'bootstrap/dist/css/bootstrap.min.css';
import { Chart as ChartJS } from 'chart.js/auto'
import { Line } from "react-chartjs-2";

import Country_info from "../Country/Country_info";


  const colourOptions = [
    { value: "India", label: "India" },
    { value: "China", label: "China" },
    { value: "Russia", label: "Russia" },
    { value: "Australia", label: "Australia"},
    { value: "Canada", label: "Canada"}
  ];

  const statOptions = [
    { value : 1 , label : "Deaths" },
    { value : 2, label : "Active cases"}


  ]

  const Option = (props) => {
    return (
      <div>
        <components.Option {...props}>
          <input
            type="checkbox"
            checked={props.isSelected}
            onChange={() => null}
          />{" "}
          <label>{props.label}</label>
        </components.Option>
      </div>
    );
  };


  const totalDuration = 1000;
  const delayBetweenPoints = totalDuration / 5;
  const previousY = (ctx) => ctx.index === 0 ? ctx.chart.scales.y.getPixelForValue(100) : ctx.chart.getDatasetMeta(ctx.datasetIndex).data[ctx.index - 1].getProps(['y'], true).y;
  const animation = {
    x: {
      type: 'number',
      easing: 'linear',
      duration: delayBetweenPoints,
      from: NaN, // the point is initially skipped
      delay(ctx) {
        if (ctx.type !== 'data' || ctx.xStarted) {
          return 0;
        }
        ctx.xStarted = true;
        return ctx.index * delayBetweenPoints;
      }
    },
    y: {
      type: 'number',
      easing: 'linear',
      duration: delayBetweenPoints,
      from: previousY,
      delay(ctx) {
        if (ctx.type !== 'data' || ctx.yStarted) {
          return 0;
        }
        ctx.yStarted = true;
        return ctx.index * delayBetweenPoints;
      }
    }
  };

class Stats_info extends React.Component{

    constructor(props){

        super(props); 

        this.state = {
            name : "India",
            Country_Selected: [],
            Stat_options : [],
            all_req : false,
            date_range : [],
            graph_data : null 

        }






        this.get_data = this.get_data.bind(this) ;
        this.handleChange = this.handleChange.bind(this) ;
        this.handleChange2 = this.handleChange2.bind(this) ;
        this.handleSelect = this.handleSelect.bind(this) ;

        this.display_graph = this.display_graph.bind(this) ;

    }

    handleChange = selected => {
        this.setState({
          Country_Selected: selected
        });





      };

    handleChange2 = selected => {
      this.setState({

        Stat_options : selected


      })


    }

    handleSelect(date){
      console.log("date selector",date); // native Date object

      this.setState({
        date_range: date
      })


  }


    get_data(){


      this.setState({
        all_req : false 
      })
      console.log("called get data") ;

      console.log(this.state.Country_Selected) ;

      console.log(this.state.date_range) ;

      var graph_data = {
        labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun"],
        datasets: []
      };

      graph_data.datasets= [];

      console.log('from dataset',graph_data.datasets.length);

      let countries = this.state.Country_Selected ;
      let p = Object.values(this.state.date_range)[0];
      let p3 = JSON.stringify(p.endDate);
        let p2 = JSON.stringify(p.startDate);
      for(var i = 0 ; i < countries.length ; i++ ){

        console.log(countries[i].value);

        var this_data = [] ; var tp ;
        fetch(`http://10.42.0.51:8000/country/${countries[i].value}/type/new_cases/variant/alpha/start/${p2}/end/${p3}`)
        .then((Response) => Response.json())
        .then((json) => {

            tp  = JSON.parse(json);

            console.log(tp);
            for(var j =0 ;j <tp.length;j++){

              this_data.push(parseInt(tp[j][0]));




            }




        })




        graph_data.datasets[i] =  {
          label: countries[i].value,
          data: this_data

        }

        console.log("Final data to display", graph_data.datasets[i]);




      }



      this.setState({

        graph_data : graph_data ,
        all_req : true 

      })




    }

    display_graph(){

      if(!this.state.all_req) return(<>Waiting for data</>) ;


      console.log("data from state", this.state.graph_data) ;
      return(

        <>


          <h1>{this.state.graph_data.datasets.length}</h1>

          <Line  data={this.state.graph_data}



          />

        </>

      )

    }

    componentDidMount(){

        // do nothing as of now 

    }

    render(){
      const selectionRange = {
        startDate: new Date(),
        endDate: new Date(),
        key: 'selection',
      }

        return(

            <>

              {< this.display_graph />} 

                <h1> Stats page </h1>


                <Button onClick={this.get_data} > Get the data</Button>
                <p>Select Countries</p>
                <span
        className="d-inline-block"
        data-toggle="popover"
        data-trigger="focus"
        data-content="Please selecet account(s)"
      >
        <ReactSelect
          options={colourOptions}
          isMulti
          closeMenuOnSelect={false}
          hideSelectedOptions={false}
          components={{
            Option
          }}
          onChange={this.handleChange}
          allowSelectAll={true}
          value={this.state.optionSelected}
        />
      </span>

          <br></br>
          <p>Select Statistic</p>
      <span
        className="d-inline-block"
        data-toggle="popover"
        data-trigger="focus"
        data-content="Please selecet account(s)"
      >
        <ReactSelect
          options={statOptions}

          closeMenuOnSelect={false}
          hideSelectedOptions={false}
          components={{
            Option
          }}
          onChange={this.handleChange2}

          value={this.state.Stat_options}
        />
      </span>

      <p> Select the date range </p>
      <DateRangePicker
        ranges={[selectionRange]}
        onChange={this.handleSelect}
      />



            </>





        )
    }

}



export default (props) => (
    <Stats_info
        {...props}
        params={useParams()}
    />
);
